Necesito test unitarias para este componente de react: ```tsx
import { CheckCircleOutline, PauseCircleOutline } from '@mui/icons-material'
import dayjs from 'dayjs'
import React from 'react'
import { useTranslation } from 'react-i18next'

import { Tag } from '../../../../../../components/common/Tag/Tag'
import { BenefitFundsIcon } from '../../../components/BenefitFundsIcon/BenefitFundsIcon'
import { isGiftCardFundType } from '../../../utils/benefitFund'
import { usePageBenefitFundsDetail } from '../usePageBenefitFundsDetail'
import { Metas } from './Metas/Metas'

export const Summary = () => {
  const { t } = useTranslation()
  const { benefitFundDetails } = usePageBenefitFundsDetail()

  const isFinished = benefitFundDetails?.end_date
    ? dayjs().subtract(1, 'day').isAfter(dayjs(benefitFundDetails.end_date))
    : false

  return (
    <>
      {benefitFundDetails && (
        <div className='flex flex-col gap-2'>
          <BenefitFundsIcon
            color={benefitFundDetails.color}
            icon={benefitFundDetails.emoji}
          />
          <div className='flex items-center gap-2'>
            <h1 className='text-2xl font-semibold text-slate-900'>
              {benefitFundDetails?.name}
            </h1>
            {isGiftCardFundType(benefitFundDetails.fund_type) && (
              <Tag
                text={t('admin.benefitFunds.detail.summary.fundType.label')}
              />
            )}
            {benefitFundDetails.distribution_suspended && (
              <Tag
                color='red'
                leftIcon={PauseCircleOutline}
                text={t(
                  'admin.benefitFunds.detail.summary.distribution.suspended'
                )}
              />
            )}
            {isFinished && (
              <Tag
                color='leeto-blue'
                leftIcon={CheckCircleOutline}
                text={t(
                  'admin.benefitFunds.detail.summary.distribution.finished'
                )}
              />
            )}
          </div>
          <Metas benefitFundDetails={benefitFundDetails} />
        </div>
      )}
    </>
  )
}

```


```tsx
import { render, screen } from '@testing-library/react'
import { Summary } from './Summary'
import { usePageBenefitFundsDetail } from '../usePageBenefitFundsDetail'

jest.mock('../usePageBenefitFundsDetail')

describe('Summary', () => {
  it('renders the benefit fund name', () => {
    const benefitFundDetails = { name: 'Test Benefit Fund' }
    usePageBenefitFundsDetail.mockReturnValue({ benefitFundDetails })

    render(<Summary />)

    expect(screen.getByText('Test Benefit Fund')).toBeInTheDocument()
  })

  it('renders the tag for gift card fund type', () => {
    const benefitFundDetails = { fund_type: 'gift_card' }
    usePageBenefitFundsDetail.mockReturnValue({ benefitFundDetails })

    render(<Summary />)

    expect(screen.getByText('Fund Type')).toBeInTheDocument()
  })

  it('renders the tag for suspended distribution', () => {
    const benefitFundDetails = { distribution_suspended: true }
    usePageBenefitFundsDetail.mockReturnValue({ benefitFundDetails })

    render(<Summary />)

    expect(screen.getByText('Distribution Suspended')).toBeInTheDocument()
  })

  it('renders the tag for finished distribution', () => {
    const benefitFundDetails = { end_date: new Date() }
    usePageBenefitFundsDetail.mockReturnValue({ benefitFundDetails })

    render(<Summary />)

    expect(screen.getByText('Distribution Finished')).toBeInTheDocument()
  })
})
